import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-hot-toast';

type Member = {
  id: string;
  fullName: string;
  role: string;
};

type Project = {
  id: string;
  title: string;
  description: string;
  status: string;
  startDate: string;
  endDate?: string;
  creationDate: string;
  categoryName: string;
  members: Member[];
};

export default function ProjectPage() {
  const { id } = useParams();
  const [project, setProject] = useState<Project | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`/project/${id}/get`)
      .then(res => setProject(res.data))
      .catch(() => toast.error('Ошибка при загрузке проекта'))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <p className="text-center mt-10">Загрузка...</p>;
  if (!project) return <p className="text-center mt-10">Проект не найден</p>;

  return (
    <div className="max-w-3xl mx-auto mt-10 p-6 bg-white shadow rounded space-y-4">
      <h1 className="text-3xl font-bold">{project.title}</h1>
      <p className="text-gray-600">{project.description}</p>

      <div className="grid grid-cols-2 gap-4 mt-4 text-sm text-gray-700">
        <div><strong>Статус:</strong> {project.status}</div>
        <div><strong>Категория:</strong> {project.categoryName}</div>
        <div><strong>Дата начала:</strong> {project.startDate}</div>
        <div><strong>Дата окончания:</strong> {project.endDate || 'Не указана'}</div>
        <div><strong>Дата создания:</strong> {project.creationDate}</div>
      </div>

      <div>
        <h2 className="text-xl font-semibold mt-6 mb-2">Участники проекта</h2>
        {project.members.length === 0 ? (
          <p>Пока нет участников</p>
        ) : (
          <ul className="list-disc list-inside">
            {project.members.map(member => (
              <li key={member.id}>{member.fullName}{member.role}</li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
