import { useState, useEffect } from "react";
import axios from "axios";
import ProfileEdit from "./ProfileEdit";
import { toast } from "react-hot-toast";
import { useNavigate } from "react-router-dom";

type UserProfile = {
  bio: string;
  picture: string;
};

export default function ProfileEditPage() {
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProfile() {
      try {
        const res = await axios.get("/user/profile/current", { withCredentials: true }); // или другой endpoint
        setProfile(res.data);
      } catch {
        toast.error("Не удалось загрузить профиль для редактирования");
      } finally {
        setLoading(false);
      }
    }
    fetchProfile();
  }, []);

  const handleSave = (updatedProfile: UserProfile) => {
    setProfile(updatedProfile);
    toast.success("Профиль обновлен");
    navigate("/profile"); // возврат на страницу профиля
  };

  const handleCancel = () => {
    navigate("/profile"); // возврат без изменений
  };

  if (loading) return <div>Загрузка...</div>;
  if (!profile) return <div>Профиль не найден</div>;

  return (
    <ProfileEdit
      initialProfile={profile}
      onSave={handleSave}
      onCancel={handleCancel}
    />
  );
}
