import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

type Member = {
  userId: string;
  userName: string;
  role: string;
};

type Project = {
  id: string;
  title: string;
  description: string;
  status: string;
  startDate: string;
  endDate?: string;
  creationDate: string;
  categoryName: string;
  members: Member[];
};
type JoinRequestDto = {
  id: string;
  userId: string;
  userName: string;
  message: string;
  status: string;
  createdAt: string;
};

export default function ProjectPage() {
  const { id } = useParams();
  const [project, setProject] = useState<Project | null>(null);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();
  const isMember = project?.members.some(m => m.userId === user?.id);
  const currentUserRole = project?.members.find(m => m.userId === user?.id)?.role;
  const isManager = currentUserRole === 'ProjectManager';
  const [showModal, setShowModal] = useState(false);
const [joinMessage, setJoinMessage] = useState('');
const [requests, setRequests] = useState<JoinRequestDto[]>([]);
const [requestLoading, setRequestLoading] = useState(false);

  const handleJoinRequest = async () => {
  try {
    await axios.post(`/join-request/${project?.id}`, { message: joinMessage });
    toast.success('Заявка отправлена');
    setShowModal(false);
    setJoinMessage('');
  } catch {
    toast.error('Ошибка при отправке заявки или заявка уже отправлена');
  }
};

const handleAccept = async (requestId: string) => {
  try {
    await axios.post(`/join-request/${requestId}/approve`, { role: 'Contributor' });
    toast.success('Заявка одобрена');
    setRequests(prev => prev.filter(r => r.id !== requestId));
  } catch {
    toast.error('Ошибка при одобрении заявки');
  }
};

const handleReject = async (requestId: string) => {
  try {
    await axios.post(`/join-request/${requestId}/reject`, { reason: 'Не подходим' });
    toast.success('Заявка отклонена');
    setRequests(prev => prev.filter(r => r.id !== requestId));
  } catch {
    toast.error('Ошибка при отклонении заявки');
  }
};

useEffect(() => {
  if (!isManager) return;

  setRequestLoading(true);
  axios.get(`/join-request/${project?.id}/requests`)
    .then(res => setRequests(res.data))
    .catch(() => toast.error('Ошибка при загрузке заявок'))
    .finally(() => setRequestLoading(false));
}, [isManager, project?.id]);

  useEffect(() => {
    axios.get(`/project/${id}/get`)
      .then(res => setProject(res.data))
      .catch(() => toast.error('Ошибка при загрузке проекта'))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <p className="text-center mt-10">Загрузка...</p>;
  if (!project) return <p className="text-center mt-10">Проект не найден</p>;

  return (
    <div className="max-w-3xl mx-auto mt-10 p-6 bg-white shadow rounded space-y-4">
      <h1 className="text-3xl font-bold">{project.title}</h1>
      <p className="text-gray-600">{project.description}</p>

      <div className="grid grid-cols-2 gap-4 mt-4 text-sm text-gray-700">
        <div><strong>Статус:</strong> {project.status}</div>
        <div><strong>Категория:</strong> {project.categoryName}</div>
        <div><strong>Дата начала:</strong> {project.startDate}</div>
        <div><strong>Дата окончания:</strong> {project.endDate || 'Не указана'}</div>
        <div><strong>Дата создания:</strong> {project.creationDate}</div>
      </div>

      <div>
        <h2 className="text-xl font-semibold mt-6 mb-2">Участники проекта</h2>
        {project.members.length === 0 ? (
          <p>Пока нет участников</p>
        ) : (
          <ul className="list-disc list-inside">
            {project.members.map(member => (
              <li key={member.userId}>
                <Link
                  to={`/user/${member.userId}`}
                  className="text-blue-600 hover:underline"
                >
                  {member.userName}
                </Link>
                , {member.role}
              </li>
            ))}
          </ul>
        )}
        {!isMember && (
  <button
    onClick={() => setShowModal(true)}
    className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
  >
    Отправить заявку на вступление
  </button>
)}
{isManager && (
  <div className="mt-8">
    <h2 className="text-xl font-semibold mb-2">Заявки на вступление</h2>
    {requestLoading ? (
      <p>Загрузка заявок...</p>
    ) : requests.length === 0 ? (
      <p>Нет новых заявок</p>
    ) : (
      <ul className="space-y-2">
        {requests.map((r) => (
          <li key={r.id} className="border p-4 rounded">
            <p><strong>Пользователь:</strong> <Link to={`/user/${r.userId}`} className="text-blue-600 hover:underline">{r.userName}</Link></p>
            <p><strong>Сообщение:</strong> {r.message}</p>
            <p><strong>Дата:</strong> {r.createdAt}</p>
            <div className="mt-2 space-x-2">
              <button
                onClick={() => handleAccept(r.id)}
                className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
              >
                Принять
              </button>
              <button
                onClick={() => handleReject(r.id)}
                className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700"
              >
                Отклонить
              </button>
            </div>
          </li>
        ))}
      </ul>
    )}
  </div>
)}
{showModal && (
  <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
    <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
      <h2 className="text-xl font-semibold mb-4">Сообщение к заявке</h2>
      <textarea
        value={joinMessage}
        onChange={(e) => setJoinMessage(e.target.value)}
        className="w-full h-24 border border-gray-300 rounded p-2 mb-4 resize-none"
        placeholder="Напишите, почему хотите присоединиться..."
      />
      <div className="flex justify-end space-x-2">
        <button
          onClick={() => setShowModal(false)}
          className="px-4 py-2 border rounded hover:bg-gray-100"
        >
          Отмена
        </button>
        <button
          onClick={handleJoinRequest}
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Отправить
        </button>
      </div>
    </div>
  </div>
)}
      </div>
    </div>
  );
}
