import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-hot-toast';
import LoadingSpinner from './LoadingSpinner'; 
import { useState } from 'react';

export default function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [isLoggingOut, setIsLoggingOut] = useState(false);

  const handleLogout = async () => {
    setIsLoggingOut(true);
    try {
      await logout();
      toast.success('Вы успешно вышли из системы');
      navigate('/');
    } catch (error) {
      toast.error('Ошибка при выходе. Попробуйте еще раз');
    } finally {
      setIsLoggingOut(false);
    }
  };

  return (
    <nav className="bg-gray-800 text-white px-6 py-4 shadow-lg">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <Link 
          to="/" 
          className="text-2xl font-bold hover:text-gray-300 transition-colors"
        >
          ItUpHub
        </Link>

        <div className="flex items-center space-x-6">
          {user ? (
            <>
              <Link
                to="/profile"
                className="flex items-center space-x-3 group hover:bg-gray-700 px-4 py-2 rounded-lg transition-colors"
              >
                {user.profilePicture ? (
                  <img
                    src={`user/images/${user.profilePicture}`}
                    alt="Аватар"
                    className="w-10 h-10 rounded-full border-2 border-gray-600 object-cover"
                  />
                ) : (
                  <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
                    <span className="text-lg">
                      {user.userName[0].toUpperCase()}
                    </span>
                  </div>
                )}
                <span className="font-medium group-hover:text-gray-200">
                  {user.userName}
                </span>
              </Link>

              <button
                onClick={handleLogout}
                disabled={isLoggingOut}
                className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded-lg flex items-center space-x-2 transition-colors disabled:opacity-70"
              >
                {isLoggingOut ? (
                  <>
                    <LoadingSpinner size="small" />
                    <span>Выход...</span>
                  </>
                ) : (
                  <span>Выйти</span>
                )}
              </button>
            </>
          ) : (
            <div className="flex space-x-4">
              <Link
                to="/login"
                className="px-4 py-2 rounded-lg hover:bg-gray-700 transition-colors font-medium"
              >
                Вход
              </Link>
              <Link
                to="/register"
                className="px-4 py-2 rounded-lg bg-blue-600 hover:bg-blue-700 transition-colors font-medium"
              >
                Регистрация
              </Link>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
}
