import React, { useState } from 'react';
import axios from 'axios';

type RegisterData = {
  userName: string;  // добавлено поле UserName
  email: string;
  password: string;
  confirmPassword: string;
};

function isAxiosError(error: unknown): error is { 
  response?: { data?: { message?: string } };
  request?: any;
  message: string;
} {
  return (
    typeof error === 'object' &&
    error !== null &&
    ('response' in error || 'request' in error || 'message' in error)
  );
}

const RegisterForm: React.FC = () => {
  const [formData, setFormData] = useState<RegisterData>({
    userName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setErrorMessage(null);

    try {
      const response = await axios.post('http://localhost:5230/user/register', formData);
      console.log('Registered:', response.data);
    } catch (error: unknown) {
      if (isAxiosError(error)) {
        if (error.response) {
          setErrorMessage(error.response.data?.message || 'Ошибка с сервера');
        } else if (error.request) {
          setErrorMessage('Сервер не отвечает');
        } else {
          setErrorMessage(error.message);
        }
      } else {
        setErrorMessage('Неизвестная ошибка');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 space-y-4">
      <input
        type="text"
        name="userName"
        placeholder="Имя пользователя"
        value={formData.userName}
        onChange={handleChange}
        required
        className="w-full p-2 border border-gray-300 rounded"
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        required
        className="w-full p-2 border border-gray-300 rounded"
      />
      <input
        type="password"
        name="password"
        placeholder="Пароль"
        value={formData.password}
        onChange={handleChange}
        required
        className="w-full p-2 border border-gray-300 rounded"
      />
      <input
        type="password"
        name="confirmPassword"
        placeholder="Подтверждение пароля"
        value={formData.confirmPassword}
        onChange={handleChange}
        required
        className="w-full p-2 border border-gray-300 rounded"
      />
      {errorMessage && (
        <div className="text-red-600 bg-red-100 p-2 rounded">{errorMessage}</div>
      )}
      <button
        type="submit"
        className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
      >
        Зарегистрироваться
      </button>
    </form>
  );
};

export default RegisterForm;
