import React, { useState } from 'react';
import axios, { AxiosError } from 'axios';

type RegisterData = {
  email: string;
  password: string;
  confirmPassword: string;
};

const RegisterForm: React.FC = () => {
  const [formData, setFormData] = useState<RegisterData>({
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setErrorMessage(null);

    try {
      const response = await axios.post('http://localhost:5230/user/register', formData);
      console.log('Registered:', response.data);
      // тут можно редиректить или показывать успех
    } catch (error: unknown) {
      if (axios.isAxiosError(error)) {
        // Это ошибка из axios
        if (error.response) {
          // Ошибка пришла с сервера, например 400 или 409
          setErrorMessage(error.response.data.message || 'Ошибка с сервера');
        } else if (error.request) {
          // Запрос был отправлен, но ответа не получили
          setErrorMessage('Сервер не отвечает');
        } else {
          // Ошибка настройки запроса
          setErrorMessage(error.message);
        }
      } else {
        // Это не axios ошибка — например, какая-то другая ошибка в коде
        setErrorMessage('Неизвестная ошибка');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 space-y-4">
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        required
        className="w-full p-2 border border-gray-300 rounded"
      />
      <input
        type="password"
        name="password"
        placeholder="Пароль"
        value={formData.password}
        onChange={handleChange}
        required
        className="w-full p-2 border border-gray-300 rounded"
      />
      <input
        type="password"
        name="confirmPassword"
        placeholder="Подтверждение пароля"
        value={formData.confirmPassword}
        onChange={handleChange}
        required
        className="w-full p-2 border border-gray-300 rounded"
      />
      {errorMessage && (
        <div className="text-red-600 bg-red-100 p-2 rounded">{errorMessage}</div>
      )}
      <button
        type="submit"
        className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
      >
        Зарегистрироваться
      </button>
    </form>
  );
};

export default RegisterForm;
