          value={title}
          onChange={e => setTitle(e.target.value)}
          required
          className="w-full border p-2 rounded"
        />

        <textarea
          placeholder="–û–ø–∏—Å–∞–Ω–∏–µ"
          value={description}
          onChange={e => setDescription(e.target.value)}
          required
          className="w-full border p-2 rounded"
        />

        <select
          value={categoryId}
          onChange={e => setCategoryId(e.target.value)}
          required
          className="w-full border p-2 rounded"
