import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-hot-toast';
import { useAuth } from '../context/AuthContext';
import { getErrorMessage } from '../utils/errorMessages';

type LoginData = {
  email: string;
  password: string;
};

export default function Login() {
  const [form, setForm] = useState<LoginData>({ email: '', password: '' });
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      await axios.post('/user/login', form, { withCredentials: true });
       // важно withCredentials
      const { data: userData } = await axios.get('/user/current', {
      withCredentials: true
    });

    login({
      id: userData.id,
      userName: userData.userName,
      email: userData.email,
      profilePicture: userData.picture 
        ? `${userData.picture}` // Добавляем префикс
        : '',
      bio: userData.bio || '',
      skills: userData.skills || []
    });
    toast.success('Вы успешно вошли!');

    navigate('/');
    } catch (error: unknown) {
      if (axios.isAxiosError(error) && error.response?.data) {
        const message = getErrorMessage(error.response.data);
        toast.error(message);
      } else {
        toast.error('Что-то пошло не так. Попробуйте снова.');
      }
    }
  };

  return (
    <div className="max-w-md mx-auto mt-12 p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-center">Вход</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="email" className="block mb-1 font-medium">Email</label>
          <input
            id="email"
            name="email"
            type="email"
            value={form.email}
            onChange={handleChange}
            required
            className="w-full border px-3 py-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        <div>
          <label htmlFor="password" className="block mb-1 font-medium">Пароль</label>
          <input
            id="password"
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            required
            className="w-full border px-3 py-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition"
        >
          Войти
        </button>
      </form>
    </div>
  );
}
