import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function Navbar() {
  const { user, logout } = useAuth();

  return (
    <nav className="bg-gray-800 text-white p-4 flex justify-between items-center">
      <Link to="/" className="text-xl font-bold">ItUpHub</Link>

      <div className="flex items-center space-x-4">
        {user ? (
          <>
            <Link to={`/profile`} className="hover:underline">
              Профиль
            </Link>
            <button onClick={logout} className="bg-red-600 px-3 py-1 rounded hover:bg-red-700">
              Выйти
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="hover:underline">Вход</Link>
            <Link to="/register" className="hover:underline">Регистрация</Link>
          </>
        )}
      </div>
    </nav>
  );
}
