import { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-hot-toast';
import { link } from 'fs';
import { useNavigate } from 'react-router-dom';

type Profile = {
  bio: string;
  picture: string;
  userName: string;
  email: string;
};

export default function ProfilePage() {
  const { user } = useAuth();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      if (!user) {
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get(`/user/profile/${user.id}`, { withCredentials: true });
        const data = res.data;

        console.log('Fetched profile:', data);

        setProfile({
          bio: data.bio ?? '',
          picture: data.picture ?? '',
          userName: data.userName ?? '',
          email: data.email ?? '',
        });
      } catch (error) {
        console.error('Ошибка загрузки профиля:', error);
        toast.error('Не удалось загрузить профиль');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [user]);

  if (loading) return <div>Загрузка...</div>;
  if (!profile) return <div>Профиль не найден</div>;

  return (
    <div className="max-w-xl mx-auto mt-8 p-4 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Профиль пользователя</h2>

      {profile.picture && (
        <img
          src={profile.picture}
          alt="Аватар"
          className="w-32 h-32 rounded-full mx-auto mb-4 object-cover"
        />
      )}

      <p><strong>Имя пользователя:</strong> {profile.userName}</p>
      <p><strong>Email:</strong> {profile.email}</p>
      <p><strong>Биография:</strong> {profile.bio || 'Пока ничего нет...'}</p>

      <button
        className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
        onClick={() => {
          navigate(`/profile/${user?.id}`);
        }}
      >
        Редактировать профиль
      </button>
    </div>
  );
}
