import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-hot-toast';
import LoadingSpinner from './LoadingSpinner';
import axios from 'axios';
import { useEffect, useState } from 'react';

type Notification = {
  id: string;
  message: string;
  createdAt: string;
  isRead: boolean;
};

export default function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [isLoggingOut, setIsLoggingOut] = useState(false);
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [showNotifications, setShowNotifications] = useState(false);
  const unreadCount = notifications.filter(n => !n.isRead).length;

  useEffect(() => {
    axios.get('/notifications')
      .then(res => setNotifications(res.data))
      .catch(() => console.error('Ошибка загрузки уведомлений'));
  }, []);

  const handleToggleNotifications = () => {
    setShowNotifications(prev => !prev);

    // Отметить все как прочитанные при открытии
    if (!showNotifications && unreadCount > 0) {
      axios.post('/notifications/mark-all-read')
        .then(() => {
          setNotifications(prev =>
            prev.map(n => ({ ...n, isRead: true }))
          );
        });
    }
  };

  const handleLogout = async () => {
    setIsLoggingOut(true);
    try {
      await logout();
      toast.success('Вы успешно вышли из системы');
      navigate('/');
    } catch (error) {
      toast.error('Ошибка при выходе. Попробуйте еще раз');
    } finally {
      setIsLoggingOut(false);
    }
  };


  return (
    <nav className="bg-gray-800 text-white px-6 py-4 shadow-lg">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <Link 
          to="/" 
          className="text-2xl font-bold hover:text-gray-300 transition-colors"
        >
          ItUpHub
        </Link>

        <div className="flex items-center space-x-6">
          {user ? (
            <>
            <Link
      to="/my-projects"
      className="hover:bg-gray-700 px-4 py-2 rounded-lg transition-colors font-medium"
    >
      Мои проекты
    </Link>
    <div className="relative">
      <button
        onClick={handleToggleNotifications}
        className="relative text-2xl"
      >
        <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth="1.5"
    stroke="currentColor"
    className="w-6 h-6 text-gray-700"
  >
    <path
      strokeLinecap="round"
      strokeLinejoin="round"
      d="M14.25 17.25h-4.5m9-4.5V9a6 6 0 10-12 0v3.75a3.75 3.75 0 01-.75 2.25l-.75.75h15l-.75-.75a3.75 3.75 0 01-.75-2.25z"
    />
  </svg>
        {unreadCount > 0 && (
          <span className="absolute -top-1 -right-1 bg-red-600 text-white text-xs px-1.5 py-0.5 rounded-full">
            {unreadCount}
          </span>
        )}
      </button>

      {showNotifications && (
        <div className="absolute right-0 mt-2 w-160 bg-white border shadow-lg rounded z-50">
          <div className="p-4 border-b text-black font-bold">Уведомления</div>
          {notifications.length === 0 ? (
            <div className="p-4 text-gray-500">Нет уведомлений</div>
          ) : (
            <ul className="max-h-80 overflow-y-auto">
              {notifications.map(n => (
                <li
            key={n.id}
            className={`px-4 py-2 border-b ${
              n.isRead ? 'bg-white' : 'bg-blue-50 font-medium'
            }`}
            style={{ color: 'black' }} // обязательно черный текст
          >
                  <p>{n.message}</p>
                  <small className="text-gray-400">{new Date(n.createdAt).toLocaleString()}</small>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
              <Link
                to="/profile"
                className="flex items-center space-x-3 group hover:bg-gray-700 px-4 py-2 rounded-lg transition-colors"
              >
                {user.avatar ? (
                  <img
                    src={`user/images/${user.avatar}`}
                    alt="Аватар"
                    className="w-10 h-10 rounded-full border-2 border-gray-600 object-cover"
                  />
                ) : (
                  <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
                    <span className="text-lg">
                      {user.userName[0].toUpperCase()}
                    </span>
                  </div>
                )}
                <span className="font-medium group-hover:text-gray-200">
                  {user.userName}
                </span>
              </Link>


              <button
                onClick={handleLogout}
                disabled={isLoggingOut}
                className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded-lg flex items-center space-x-2 transition-colors disabled:opacity-70"
              >
                {isLoggingOut ? (
                  <>
                    <LoadingSpinner size="small" />
                    <span>Выход...</span>
                  </>
                ) : (
                  <span>Выйти</span>
                )}
              </button>
            </>
          ) : (
            <div className="flex space-x-4">
              <Link
                to="/login"
                className="px-4 py-2 rounded-lg hover:bg-gray-700 transition-colors font-medium"
              >
                Вход
              </Link>
              <Link
                to="/register"
                className="px-4 py-2 rounded-lg bg-blue-600 hover:bg-blue-700 transition-colors font-medium"
              >
                Регистрация
              </Link>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
}
