import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { Link } from 'react-router-dom';

export default function ProfilePage() {
  const { user, logout, updateUser, refreshUser } = useAuth();
  const [isUploading, setIsUploading] = useState(false);
  const [bio, setBio] = useState(user?.bio || '');
  const [tempPicture, setTempPicture] = useState(user?.profilePicture || '');
  const hasSkills = (user?.skills?.length ?? 0) > 0;
  const [showSkillModal, setShowSkillModal] = useState(false);


  useEffect(() => {
    if (user?.bio) {
      setBio(user.bio);
    }
  }, [user?.bio]);

  const handleImageUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file || !user) return;

    try {
      setIsUploading(true);
      const formData = new FormData();
      formData.append('file', file);

      const { data } = await axios.post(
        `user/${user.id}/profile-image`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
          withCredentials: true
        }
      );

      setTempPicture(data.picturePath);
      await refreshUser();
      toast.success('Аватар обновлён!');
    } catch (error) {
      toast.error('Ошибка загрузки изображения');
    } finally {
      setIsUploading(false);
    }
  };

  const handleSaveBio = async () => {
    try {
      await axios.put(`/user/${user?.id}/bio`, { bio }, {
        withCredentials: true
      });
      await refreshUser();
      toast.success('Биография обновлена');
    } catch (error) {
      toast.error('Ошибка сохранения');
    }
  };

  const handleRemoveSkill = async (skillId: string) => {
    if (!user) return;

    try {
      await axios.delete(`/user/${user.id}/skills/${skillId}`, {
        withCredentials: true,
      });
      toast.success('Навык удалён');
      await refreshUser();
    } catch (err) {
      toast.error('Ошибка при удалении навыка');
    }
  };

  return (
    <div className="max-w-3xl mx-auto mt-8 p-6 bg-white rounded-lg shadow-md">
      <div className="flex items-start gap-8">
        {/* Блок с аватаром */}
        <div className="relative group">
          <label className="cursor-pointer">
            <img
              src={`user/images/${user?.profilePicture}`}
              alt="Аватар"
              className="w-40 h-40 rounded-full border-4 border-gray-200 object-cover"
            />
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="hidden"
              disabled={isUploading}
            />
          </label>
          {isUploading && (
            <div className="absolute inset-0 bg-black/50 flex items-center justify-center rounded-full">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white"></div>
            </div>
          )}
        </div>

        {/* Информация профиля */}
        <div className="flex-1 space-y-4">
          <div>
            <h2 className="text-2xl font-bold">{user?.userName}</h2>
            <p className="text-gray-600">{user?.email}</p>
          </div>

          <div>
            <label className="block text-sm font-medium mb-2">О себе</label>
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              className="w-full p-2 border rounded-md"
              rows={4}
            />
            <button
              onClick={handleSaveBio}
              className="mt-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Сохранить
            </button>
          </div>

          {/* Скиллы */}
          {user && hasSkills && (
            <div>
              <h3 className="text-lg font-semibold mb-2">Навыки</h3>
              <div className="flex flex-wrap gap-2">
                {user!.skills.map((skill: any) => (
                  <div
                    key={skill.id.value}
                    className="relative group bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-medium hover:bg-blue-200 transition"
                  >
                    {skill.title.value}

                    <button
                      onClick={() => handleRemoveSkill(skill.id.value)}
                      className="absolute -top-2 -right-2 hidden group-hover:flex items-center justify-center w-5 h-5 bg-red-500 text-white text-xs rounded-full hover:bg-red-600"
                      title="Удалить навык"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )}
          <button
            onClick={() => setShowSkillModal(true)}
            className="inline-block mt-4 text-blue-600 hover:underline text-sm"
          >
            ➕ Добавить навыки
          </button>


          <button
            onClick={logout}
            className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
          >
            Выйти
          </button>

        </div>
        {showSkillModal && (
  <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
    <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md relative">
      <button
        onClick={() => setShowSkillModal(false)}
        className="absolute top-2 right-2 text-gray-500 hover:text-gray-700 text-xl"
      >
        &times;
      </button>

      <h2 className="text-xl font-semibold mb-4">Добавить навык</h2>

      <form
        onSubmit={async (e) => {
          e.preventDefault();
          const formData = new FormData(e.currentTarget);
          const title = formData.get('title')?.toString().trim();

          if (!title) {
            toast.error('Введите название навыка');
            return;
          }

          try {
            await axios.post(
              `/user/${user?.id}/skills`,
              { title },
              { withCredentials: true }
            );
            toast.success('Навык добавлен');
            setShowSkillModal(false);
            await refreshUser();
          } catch {
            toast.error('Ошибка при добавлении навыка');
          }
        }}
      >
        <input
          type="text"
          name="title"
          placeholder="Введите название навыка"
          className="w-full p-2 border rounded mb-4"
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Сохранить
        </button>
      </form>
    </div>
  </div>
)}
      </div>
    </div>
  );
}
