import { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-hot-toast';

type Profile = {
  bio: string;
  picture: string;
  userName: string;
  email: string;
};

export default function ProfilePage() {
  const { user } = useAuth();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      if (!user) return;

      try {
        const res = await axios.get(`/user/${user.id}`, { withCredentials: true });
        const data = res.data;

        setProfile({
          bio: data.userProfile?.bio || '',
          picture: data.userProfile?.picture || '',
          userName: data.userName,
          email: data.email,
        });
      } catch (error) {
        toast.error('Не удалось загрузить профиль');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [user]);

  if (loading) return <div>Загрузка...</div>;
  if (!profile) return <div>Профиль не найден</div>;

  return (
    <div className="max-w-xl mx-auto mt-8 p-4 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Профиль пользователя</h2>

      {profile.picture && (
        <img
          src={profile.picture}
          alt="Аватар"
          className="w-32 h-32 rounded-full mx-auto mb-4 object-cover"
        />
      )}

      <p><strong>Имя пользователя:</strong> {profile.userName}</p>
      <p><strong>Email:</strong> {profile.email}</p>
      <p><strong>Биография:</strong> {profile.bio || 'Пока ничего нет...'}</p>

      <button
        className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
        onClick={() => {
          // Например, можно перейти на страницу редактирования
        }}
      >
        Редактировать профиль
      </button>
    </div>
  );
}
