import React, { useState } from 'react';
import axios, { AxiosError } from 'axios';

function isAxiosError(error: any): error is AxiosError {
  return error.isAxiosError === true;
}

export default function Register() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: '' });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      setErrors({});
      await axios.post('http://localhost:5230/user/register', form);
      alert('Успешно зарегистрированы!');
    } catch (error: unknown) {
      if (isAxiosError(error) && error.response) {
        const data = error.response.data;
        if (data.type === 'Validation') {
          const [field, ...msgParts] = data.message.split(' ');
          setErrors({ [field]: msgParts.join(' ') });
        } else {
          alert(data.message || 'Произошла ошибка');
        }
      } else {
        alert('Ошибка сети или неизвестная ошибка');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4">
      <div className="mb-4">
        <label htmlFor="email" className="block font-bold mb-1">Email</label>
        <input
          type="email"
          name="email"
          id="email"
          value={form.email}
          onChange={handleChange}
          className={`w-full border p-2 rounded ${errors.email ? 'border-red-500' : 'border-gray-300'}`}
        />
        {errors.email && <p className="text-red-600 text-sm mt-1">{errors.email}</p>}
      </div>

      <div className="mb-4">
        <label htmlFor="password" className="block font-bold mb-1">Пароль</label>
        <input
          type="password"
          name="password"
          id="password"
          value={form.password}
          onChange={handleChange}
          className={`w-full border p-2 rounded ${errors.password ? 'border-red-500' : 'border-gray-300'}`}
        />
        {errors.password && <p className="text-red-600 text-sm mt-1">{errors.password}</p>}
      </div>

      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
        Зарегистрироваться
      </button>
    </form>
  );
}
