import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const HomePage: React.FC = () => {
  const { email, setEmail } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    // Очищаем контекст (и при необходимости можно удалить cookie)
    setEmail(null);
    navigate('/');
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Главная страница ItUpHub</h1>

      {email ? (
        <>
          <p>Вы вошли как <strong>{email}</strong></p>
          <button onClick={handleLogout} style={{ padding: '8px 16px', marginTop: 10 }}>
            Выйти
          </button>
        </>
      ) : (
        <>
          <p>Добро пожаловать! Войдите или зарегистрируйтесь:</p>
          <Link to="/login">
            <button style={{ marginRight: 10, padding: '8px 16px' }}>Вход</button>
          </Link>
          <Link to="/register">
            <button style={{ padding: '8px 16px' }}>Регистрация</button>
          </Link>
        </>
      )}
    </div>
  );
};

export default HomePage;
