import { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import { toast } from 'react-hot-toast';

type Project = {
  id: string;
  title: string;
};

export default function MyProjectsPage() {
  const { user } = useAuth();
  const [projects, setProjects] = useState<Project[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    axios.get(`/all-titles/${user.id}`, { withCredentials: true })
      .then(res => setProjects(res.data))
      .catch(() => toast.error('Ошибка загрузки проектов'))
      .finally(() => setLoading(false));
  }, [user]);

  return (
    <div className="max-w-4xl mx-auto mt-8 p-6 bg-white rounded shadow">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold">Мои проекты</h2>
        <Link
          to="/create-project"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          ➕ Создать проект
        </Link>
      </div>

      {loading ? (
        <p>Загрузка...</p>
      ) : projects.length === 0 ? (
        <div className="text-gray-600 text-center">
          <p>У вас пока нет проектов.</p>
          <p>
            <Link to="/create-project" className="text-blue-600 hover:underline">
              Создайте первый проект →
            </Link>
          </p>
        </div>
      ) : (
        <div className="space-y-4">
          {projects.map((project) => (
            <Link
              key={project.id}
              to={`/projects/${project.id}`}
              className="block border p-4 rounded hover:shadow-md transition"
            >
              <h3 className="text-lg font-semibold">{project.title}</h3>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
}
