import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import ProfileEdit from './ProfileEdit';
import { toast } from 'react-hot-toast';

type UserProfile = {
  bio: string;
  picture: string;
};

export default function ProfileEditPage() {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [profile, setProfile] = useState<UserProfile>({ bio: '', picture: '' });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) {
      toast.error('Пользователь не авторизован');
      navigate('/login');
      return;
    }

    async function fetchProfile() {
      try {
        const res = await axios.get(`/user/profile/${user.id}`, { withCredentials: true });
        setProfile({
          bio: res.data.bio || '',
          picture: res.data.picture || '',
        });
      } catch {
        toast.error('Не удалось загрузить профиль');
      } finally {
        setLoading(false);
      }
    }

    fetchProfile();
  }, [user, navigate]);

  const handleSave = (updatedProfile: UserProfile) => {
    // Можно обновить состояние, если нужно
    setProfile(updatedProfile);
    toast.success('Профиль обновлён');
    navigate('/profile');
  };

  const handleCancel = () => {
    navigate('/profile');
  };

  if (loading) return <div>Загрузка...</div>;

  return (
    <div className="max-w-xl mx-auto mt-8 p-4 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Редактировать профиль</h2>
      <ProfileEdit
        initialProfile={profile}
        onSave={handleSave}
        onCancel={handleCancel}
      />
    </div>
  );
}
