import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

import { Loader2 } from 'lucide-react';

const roleTranslations: Record<string, string> = {
  ProjectManager: 'Руководитель проекта',
  Developer: 'Разработчик',
  Designer: 'Дизайнер',
  BuisnessAnalyst: 'Бизнес-аналитик',
  Tester: 'Тестировщик',
};

type Member = {
  userId: string;
  userName: string;
  role: string;
  pictureUrl: string;
};

type Project = {
  id: string;
  title: string;
  description: string;
  status: string;
  startDate: string;
  endDate?: string;
  creationDate: string;
  categoryName: string;
  members: Member[];
};

type JoinRequestDto = {
  id: string;
  userId: string;
  userName: string;
  message: string;
  status: string;
  createdAt: string;
};

export default function ProjectPage() {
  const { id } = useParams();
  const [project, setProject] = useState<Project | null>(null);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();
  const isMember = project?.members.some(m => m.userId === user?.id);
  const currentUserRole = project?.members.find(m => m.userId === user?.id)?.role;
  const isManager = currentUserRole === 'ProjectManager';

  const [showModal, setShowModal] = useState(false);
  const [joinMessage, setJoinMessage] = useState('');
  const [requests, setRequests] = useState<JoinRequestDto[]>([]);
  const [requestLoading, setRequestLoading] = useState(false);
  const [showRoleModal, setShowRoleModal] = useState(false);
  const [selectedRequestId, setSelectedRequestId] = useState<string | null>(null);
  const [selectedRole, setSelectedRole] = useState('');

  const handleJoinRequest = async () => {
    try {
      await axios.post(`/join-request/${project?.id}`, { message: joinMessage });
      toast.success('Заявка отправлена');
      setShowModal(false);
      setJoinMessage('');
    } catch {
      toast.error('Ошибка при отправке заявки или заявка уже отправлена');
    }
  };

  const handleReject = async (requestId: string) => {
    try {
      await axios.post(`/join-request/${requestId}/reject`, { reason: 'Не подходим' });
      toast.success('Заявка отклонена');
      setRequests(prev => prev.filter(r => r.id !== requestId));
    } catch {
      toast.error('Ошибка при отклонении заявки');
    }
  };

  const handleConfirmRole = async () => {
    if (!selectedRequestId || !selectedRole) return;

    try {
      await axios.post(`/join-request/${selectedRequestId}/approve`, { role: selectedRole });
      toast.success('Заявка одобрена');
      setRequests(prev => prev.filter(r => r.id !== selectedRequestId));
      setShowRoleModal(false);
      setSelectedRequestId(null);
      setSelectedRole('');
    } catch {
      toast.error('Ошибка при одобрении заявки');
    }
  };

  useEffect(() => {
    if (!isManager) return;
    setRequestLoading(true);
    axios
      .get(`/join-request/${project?.id}/requests`)
      .then(res => setRequests(res.data))
      .catch(() => toast.error('Ошибка при загрузке заявок'))
      .finally(() => setRequestLoading(false));
  }, [isManager, project?.id]);

  useEffect(() => {
    axios
      .get(`/project/${id}/get`)
      .then(res => setProject(res.data))
      .catch(() => toast.error('Ошибка при загрузке проекта'))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading)
    return (
      <div className="flex justify-center items-center h-60">
        <Loader2 className="animate-spin w-6 h-6 text-gray-500" />
      </div>
    );

  if (!project) return <p className="text-center mt-10">Проект не найден</p>;

  return (
    <div className="max-w-4xl mx-auto mt-10 p-6 bg-white shadow-lg rounded-2xl space-y-6">
      <div>
        <h1 className="text-4xl font-bold text-gray-800 mb-2">{project.title}</h1>
        <p className="text-gray-600 text-lg">{project.description}</p>
      </div>

      <div className="grid sm:grid-cols-2 gap-4 text-gray-700 text-sm">
        <div><span className="font-semibold">Статус:</span> {project.status}</div>
        <div><span className="font-semibold">Категория:</span> {project.categoryName}</div>
        <div><span className="font-semibold">Дата начала:</span> {project.startDate}</div>
        <div><span className="font-semibold">Дата окончания:</span> {project.endDate || 'Не указана'}</div>
        <div><span className="font-semibold">Дата создания:</span> {project.creationDate}</div>
      </div>

      <section>
        <h2 className="text-2xl font-semibold mb-3">Участники проекта</h2>
        {project.members.length === 0 ? (
          <p className="text-gray-500">Пока нет участников</p>
        ) : (
          <ul className="space-y-3">
            {project.members.map(member => (
              <li key={member.userId} className="flex items-center space-x-3">
                {member.pictureUrl ? (
                  <img
                    src={`/user/images/${member.pictureUrl}`}
                    alt={member.userName}
                    className="w-10 h-10 rounded-full object-cover"
                  />
                ) : (
                  <div className="w-10 h-10 rounded-full bg-gray-400 text-white flex items-center justify-center font-bold">
                    {member.userName.charAt(0).toUpperCase()}
                  </div>
                )}
                <div>
                  <Link to={`/user/${member.userId}`} className="text-blue-600 hover:underline font-medium">
                    {member.userName}
                  </Link>
                  <div className="text-sm text-gray-500">{roleTranslations[member.role] || member.role}</div>
                </div>
              </li>
            ))}
          </ul>
        )}

        {!isMember && (
          <button
            onClick={() => setShowModal(true)}
            className="mt-5 px-5 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
          >
            Отправить заявку на вступление
          </button>
        )}
      </section>

      {isManager && (
        <section className="mt-8">
          <h2 className="text-2xl font-semibold mb-4">Заявки на вступление</h2>
          {requestLoading ? (
            <p>Загрузка заявок...</p>
          ) : requests.length === 0 ? (
            <p className="text-gray-500">Нет новых заявок</p>
          ) : (
            <ul className="space-y-4">
              {requests.map(r => (
                <li key={r.id} className="border p-4 rounded-lg shadow-sm">
                  <p><strong>Пользователь:</strong> <Link to={`/user/${r.userId}`} className="text-blue-600 hover:underline">{r.userName}</Link></p>
                  <p><strong>Сообщение:</strong> {r.message}</p>
                  <p><strong>Дата:</strong> {r.createdAt}</p>
                  <div className="mt-3 flex gap-2">
                    <button
                      onClick={() => {
                        setSelectedRequestId(r.id);
                        setShowRoleModal(true);
                      }}
                      className="bg-green-600 text-white px-4 py-1.5 rounded hover:bg-green-700"
                    >
                      Принять
                    </button>
                    <button
                      onClick={() => handleReject(r.id)}
                      className="bg-red-600 text-white px-4 py-1.5 rounded hover:bg-red-700"
                    >
                      Отклонить
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </section>
      )}

      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-6 rounded-lg shadow-md w-full max-w-md">
            <h2 className="text-xl font-semibold mb-4">Сообщение к заявке</h2>
            <textarea
              value={joinMessage}
              onChange={(e) => setJoinMessage(e.target.value)}
              className="w-full h-24 border border-gray-300 rounded p-2 mb-4 resize-none"
              placeholder="Напишите, почему хотите присоединиться..."
            />
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => setShowModal(false)}
                className="px-4 py-2 border rounded hover:bg-gray-100"
              >
                Отмена
              </button>
              <button
                onClick={handleJoinRequest}
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              >
                Отправить
              </button>
            </div>
          </div>
        </div>
      )}

      {showRoleModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md relative">
            <button
              onClick={() => setShowRoleModal(false)}
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700 text-xl"
            >
              &times;
            </button>

            <h2 className="text-xl font-semibold mb-4">Назначить роль</h2>

            <select
              value={selectedRole}
              onChange={(e) => setSelectedRole(e.target.value)}
              className="w-full mb-4 p-2 border rounded"
            >
              <option value="">Выберите роль</option>
              <option value="Developer">Разработчик</option>
              <option value="Designer">Дизайнер</option>
              <option value="Tester">Тестировщик</option>
              <option value="ProjectManager">Менеджер проекта</option>
              <option value="BuisnessAnalyst">Бизнес-аналитик</option>
            </select>

            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowRoleModal(false)}
                className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded"
              >
                Отмена
              </button>
              <button
                onClick={handleConfirmRole}
                className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded"
              >
                Подтвердить
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
