import { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';

type Profile = {
  bio: string;
  picture: string;
  userName: string;
  email: string;
};

export default function ProfilePage() {
  const { user, updateUser } = useAuth();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [loading, setLoading] = useState(true);
  const [isUploading, setIsUploading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      if (!user) {
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get(`/user/profile/${user.id}`, { 
          withCredentials: true 
        });

        setProfile({
          bio: res.data.bio ?? '',
          picture: res.data.picture ?? '',
          userName: res.data.userName ?? '',
          email: res.data.email ?? '',
        });
      } catch (error) {
        toast.error('Не удалось загрузить профиль');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [user]);

  const handleImageUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file || !user) return;

    if (!file.type.startsWith('image/')) {
      toast.error('Разрешены только изображения');
      return;
    }

    if (file.size > 5 * 1024 * 1024) {
      toast.error('Файл должен быть меньше 5MB');
      return;
    }

    try {
      setIsUploading(true);
      const formData = new FormData();
      formData.append('file', file);

      const { data } = await axios.post(
        `/user/${user.id}/profile-image`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
          withCredentials: true,
        }
      );

      // Обновляем локальное состояние и контекст
      setProfile(prev => ({
        ...prev!,
        picture: data.picturePath
      }));

      updateUser({
        ...user,
        profilePicture: data.picturePath
      });

      toast.success('Аватар обновлён!');
    } catch (error) {
      toast.error('Ошибка при загрузке изображения');
    } finally {
      setIsUploading(false);
    }
  };

  if (loading) return <div className="text-center mt-8">Загрузка профиля...</div>;
  if (!profile) return <div className="text-center mt-8">Профиль не найден</div>;

  return (
    <div className="max-w-3xl mx-auto mt-8 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Профиль пользователя</h2>

      <div className="flex items-start space-x-8">
        {/* Блок с аватаром */}
        <div className="relative group">
          <label 
            className="cursor-pointer"
            title="Нажмите для смены аватара"
          >
            <img
              src={profile.picture 
                ? `/api/images/${profile.picture}`
                : '/default-avatar.png'}
              alt="Аватар"
              className="w-40 h-40 rounded-full object-cover border-4 border-gray-200 
                       hover:border-blue-500 transition-all duration-300"
            />
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="hidden"
              disabled={isUploading}
            />

            {/* Индикатор загрузки */}
            {isUploading && (
              <div className="absolute inset-0 bg-black bg-opacity-50 rounded-full 
                            flex items-center justify-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white"></div>
              </div>
            )}
          </label>
        </div>

        {/* Информация профиля */}
        <div className="flex-1">
          <div className="mb-6">
            <h3 className="text-xl font-semibold text-gray-700">Имя пользователя</h3>
            <p className="text-gray-900 mt-1">{profile.userName}</p>
          </div>

          <div className="mb-6">
            <h3 className="text-xl font-semibold text-gray-700">Email</h3>
            <p className="text-gray-900 mt-1">{profile.email}</p>
          </div>

          <div className="mb-6">
            <h3 className="text-xl font-semibold text-gray-700">О себе</h3>
            <p className="text-gray-900 mt-1">
              {profile.bio || 'Пока нет информации о себе...'}
            </p>
          </div>

          <button
            onClick={() => navigate(`/profile/edit/${user?.id}`)}
            className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2.5 rounded-lg
                     transition-colors duration-200 flex items-center"
          >
            <svg 
              className="w-5 h-5 mr-2" 
              fill="none" 
              stroke="currentColor" 
              viewBox="0 0 24 24"
            >
              <path 
                strokeLinecap="round" 
                strokeLinejoin="round" 
                strokeWidth={2} 
                d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z" 
              />
            </svg>
            Редактировать профиль
          </button>
        </div>
      </div>
    </div>
  );
}
