import { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-hot-toast';
import clsx from 'clsx';

type NotificationDto = {
  id: string;
  message: string;
  isRead: boolean;
  createdAt: string;
};

export default function NotificationsPage() {
  const [notifications, setNotifications] = useState<NotificationDto[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('/notifications')
      .then(res => setNotifications(res.data))
      .catch(() => toast.error('Ошибка при загрузке уведомлений'))
      .finally(() => setLoading(false));
  }, []);

  const markAsRead = async (id: string) => {
    try {
      await axios.post(`/notifications/${id}/read`);
      setNotifications(prev =>
        prev.map(n => n.id === id ? { ...n, isRead: true } : n)
      );
    } catch {
      toast.error('Ошибка при пометке как прочитано');
    }
  };

  if (loading) return <p className="text-center mt-10">Загрузка...</p>;

  return (
    <div className="max-w-2xl mx-auto mt-10 p-4 bg-white shadow rounded space-y-4">
      <h1 className="text-2xl font-bold">Уведомления</h1>
      {notifications.length === 0 ? (
        <p>Нет уведомлений</p>
      ) : (
        <ul className="space-y-2">
          {notifications.map(notification => (
            <li
              key={notification.id}
              className={clsx(
                "p-4 rounded border",
                notification.isRead ? "bg-gray-100" : "bg-blue-50 border-blue-300"
              )}
            >
              <div className="flex justify-between items-center">
                <div>
                  <p>{notification.message}</p>
                  <p className="text-sm text-gray-500">{new Date(notification.createdAt).toLocaleString()}</p>
                </div>
                {!notification.isRead && (
                  <button
                    onClick={() => markAsRead(notification.id)}
                    className="text-sm text-blue-600 hover:underline"
                  >
                    Пометить как прочитано
                  </button>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
