import { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

type User = {
  id: string;
  userName: string;
  email: string;
  profilePicture: string;
  bio: string;
};

type AuthContextType = {
  user: User | null;
  login: (user: User) => void;
  logout: () => Promise<void>;
  updateUser: (updates: Partial<User>) => void;
};

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider = ({ children }: { children: React.ReactNode }) => {
  const [user, setUser] = useState<User | null>(null);
  const navigate = useNavigate();

  const login = (user: User) => {
    setUser(user);
  };

  const updateUser = (updates: Partial<User>) => {
    if (user) {
      setUser({ ...user, ...updates });
    }
  };

  const logout = async () => {
    try {
      // 1. Отправляем запрос на бэкенд для выхода
      await axios.post("/user/logout", {}, { 
        withCredentials: true 
      });
    } catch (error) {
      console.error("Logout error:", error);
    } finally {
      // 2. Очищаем состояние в любом случае
      setUser(null);
      // 3. Перенаправляем на главную
      navigate("/");
    }
  };

  useEffect(() => {
    const fetchCurrentUser = async () => {
      try {
        const res = await axios.get("/user/current", { 
          withCredentials: true 
        });

        setUser({
          id: res.data.id,
          userName: res.data.userName,
          email: res.data.email,
          profilePicture: res.data.profilePicture,
          bio: res.data.bio
        });
      } catch (error) {
        setUser(null);
      }
    };

    fetchCurrentUser();
  }, []);

  return (
    <AuthContext.Provider value={{ user, updateUser, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Убедись, что хук экспортируется как именованный экспорт
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within AuthProvider");
  }
  return context;
};
